import React,{ Component } from 'react';
import { Route } from 'react-router-dom';
import CheckoutSummary from '../../components/order/checkoutsummary/CheckoutSummary';
import ContactData from './contactdata/ContactData';

class Checkout extends Component{
  state={
    ingredent:null,
    price:0
  }
  componentWillMount(){
    const query=new URLSearchParams(this.props.location.search);
    const ingredent={};
    let price=0;
    for(let param of query.entries()){
      //['salad','1']
      if(param[0]==='price')
      {
        price=param[1];
      }
      else{
        ingredent[param[0]]=+param[1];
      }
    }
    this.setState({ingredent:ingredent,totalPrice:price});
  }
  checkoutCancelledHandler=()=>{
    this.props.history.goBack();
  }
  checkoutContinuedHandeler=()=>{
    this.props.history.replace('/checkout/contact-data');
  }
  render(){
    return (
      <div>
      <CheckoutSummary ingredent={this.state.ingredent}
      checkoutCancelled={this.checkoutCancelledHandler}
      checkoutContinued={this.checkoutContinuedHandeler}/>
      <Route
      path={this.props.match.path + '/contact-data'}
      render={()=>(<ContactData ingredent={this.state.ingredent} price={this.state.totalPrice}/>)}/>
      </div>
    );
  }

}

export default Checkout;
