import React, { Component } from 'react';
import Aux from '../../hoc/aux/Aux';
import Burger from '../../components/burger/Burger';
import BuildControls from '../../components/burger/buildcontrols/BuildControls';
import Modal from '../../components/ui/modal/Modal';
import OrderSummary from  '../../components/burger/ordersummary/OrderSummary';
import Spinner from '../../components/ui/spinner/Spinner';
import withErrorHandeler from '../../hoc/witherrorhandeller/WithErrorHandeller';
import axios from '../../Axios-Orders';

const ingredentPrice={
  salad:5,
  cheese:4,
  meat:10,
  bacon:7
}
class BurgerBuilder extends Component
{
  state ={
    ingredent:null,
    totalPrice:40,
    purchasable:false,
    purchasing:false,
    loading:false
  }

  componentDidMount()
  {
    axios.get('https://react-my-burger-91338.firebaseio.com/ingredients.json')
    .then(response=>{
      this.setState({ingredent:response.data});
    })
  }

  updatePurchaseState(ingredent)
  {
    const sum=Object.keys(ingredent)
    .map(igKey =>{
      return ingredent[igKey];
    })
    .reduce((sum,el)=>{
      return sum+el;
    },0);
    this.setState({purchasable:sum>0});
  }
  addIngredentHandler = (type) =>{
    const oldCount=this.state.ingredent[type];
    const updatedCount=oldCount+1;
    const updatedIngredents={
      ...this.state.ingredent
    };

    updatedIngredents[type]=updatedCount;
    const priceAddition=ingredentPrice[type];
    const oldPrice=this.state.totalPrice;
    const newPrice=oldPrice+priceAddition;
    this.setState({totalPrice:newPrice,ingredent:updatedIngredents});
    this.updatePurchaseState(updatedIngredents);
  }

  purchaseHandeler = () => {
    this.setState({purchasing:true});
  }
  purchaseCancelHandeler=()=>{
    this.setState({purchasing:false});
  }

  purchaseContinueHandeler=() =>
  {
    this.setState({loading:true});
    const order={
      ingredient:this.state.ingredents,
      price:this.state.totalPrice,
      customer:{
        name:'Siddharth',
        address:{
          street:'10',
          zipCode:'41351',
          country:'India'
        },
        email:'siddharth@gmail.com',
      },
      deliveryMethod:'COD'
    }
  {   axios.post('/orders.json',order)
    .then(response=>{
      this.setState({loading:false,purchasing:false});
    })
    .catch(error=>{
        this.setState({loading:false,purchasing:false})
    });}
  }
    removeIngredentHandler = (type) =>{
    const oldCount=this.state.ingredent[type];
    if(oldCount <=0)
    {
      return;
    }
    const updatedCount=oldCount-1;
    const updatedIngredents={
      ...this.state.ingredent
    };
    updatedIngredents[type]=updatedCount;
    const priceDeduction=ingredentPrice[type];
    const oldPrice=this.state.totalPrice;
    const newPrice=oldPrice-priceDeduction;
    this.setState({totalPrice:newPrice,ingredent:updatedIngredents});
    this.updatePurchaseState(updatedIngredents);
  }

  render(){
    const disabledInfo={
      ...this.state.ingredent
    };
    for(let key in disabledInfo)
    {
      disabledInfo[key]=disabledInfo[key]<=0
    }
    let orderSummary=    <OrderSummary ingredent={this.state.ingredent}
        price={this.state.totalPrice}
        purchasedCanceled={this.purchaseCancelHandeler}
        purchaseContinued={this.purchaseContinueHandeler}/>;
    if(this.state.loading){
      orderSummary=<Spinner/>;
    }
    let burger=(
      <Aux>
      <Burger ingredent={this.state.ingredent}/>
      <BuildControls
      ingredentAdded={this.addIngredentHandler}
      ingredentRemoved={this.removeIngredentHandler}
      disabled={disabledInfo}
      purchasable={this.state.purchasable}
      ordered={this.purchaseHandeler}
      price={this.state.totalPrice}
      />
      </Aux>
    );
    return(
      <Aux>
      <Modal show={this.state.purchasing} modalClosed={this.purchaseCancelHandeler}>
        {orderSummary}
      </Modal>
      </Aux>
    );
  }
}
 export default withErrorHandeler(BurgerBuilder,axios);
